mapper.update:
update challenge_application_tb
set
    accept_state = 1
where
    user_id = #{userId}
and challenge_id = #{challengeId}

update challenge_application_tb
set
    accept_state = 2
where
    user_id = #{userId}
and challenge_id = #{challengeId}

mapper.delete:
delete
from
    challenge_like_tb
where
    challenge_id = #{challengeId}
and user_id = #{userId}

delete
from
    challenge_application_tb
where
    user_id = #{userId}
and challenge_id = #{challengeId}
